function results = getResults(obj,jobid)
%getResults(obj,jobid)
%
%   Get the available result types for a completed job (see http://www.ebi.ac.uk/Tools/webservices/services/maxsprout#getresults_jobid)
%   
%     Input:
%       jobid = (string)
%   
%     Output:
%       results{:} = (WSFile)

% Build up the argument lists.
values = { ...
   jobid, ...
   };
names = { ...
   'jobid', ...
   };
types = { ...
   '{http://www.w3.org/2001/XMLSchema}string', ...
   };

% Create the message, make the call, and convert the response into a variable.
soapMessage = createSoapMessage( ...
    'http://www.ebi.ac.uk/WSMaxsprout', ...
    'getResults', ...
    values,names,types,'rpc');
response = callSoapService( ...
    obj.endpoint, ...
    'http://www.ebi.ac.uk/WSMaxsprout#getResults', ...
    soapMessage);
results = parseSoapResponse(response);
