function output = poll(obj,jobid,type)
%poll(obj,jobid,type)
%
%   Get the results of a job (see http://www.ebi.ac.uk/Tools/webservices/services/maxsprout#poll_jobid_type)
%   
%     Input:
%       jobid = (string)
%       type = (string)
%   
%     Output:
%       output = (base64Binary)

% Build up the argument lists.
values = { ...
   jobid, ...
   type, ...
   };
names = { ...
   'jobid', ...
   'type', ...
   };
types = { ...
   '{http://www.w3.org/2001/XMLSchema}string', ...
   '{http://www.w3.org/2001/XMLSchema}string', ...
   };

% Create the message, make the call, and convert the response into a variable.
soapMessage = createSoapMessage( ...
    'http://www.ebi.ac.uk/WSMaxsprout', ...
    'poll', ...
    values,names,types,'rpc');
response = callSoapService( ...
    obj.endpoint, ...
    'http://www.ebi.ac.uk/WSMaxsprout#poll', ...
    soapMessage);
output = parseSoapResponse(response);
