function jobid = runMaxsprout(obj,params,content)
%runMaxsprout(obj,params,content)
%
%   Submit a MaxSprout job (see http://www.ebi.ac.uk/Tools/webservices/services/maxsprout#runmaxsprout_params_content)
%   
%     Input:
%       params = (inputParams)
%       content{:} = (data)
%   
%     Output:
%       jobid = (string)

% Build up the argument lists.
values = { ...
   params, ...
   content, ...
   };
names = { ...
   'params', ...
   'content', ...
   };
types = { ...
   'inputParams', ...
   'tns:data[]', ...
   };

% Create the message, make the call, and convert the response into a variable.
soapMessage = createSoapMessage( ...
    'http://www.ebi.ac.uk/WSMaxsprout', ...
    'runMaxsprout', ...
    values,names,types,'rpc');
response = callSoapService( ...
    obj.endpoint, ...
    'http://www.ebi.ac.uk/WSMaxsprout#runMaxsprout', ...
    soapMessage);
jobid = parseSoapResponse(response);
